sp_widget.client_script:
function todosApprovalController($scope,spModal, spUtil) {
	/* widget controller */
	var c =this;
		c.showCommentBox = false;

	c.showHistory = false;
	c.historyButton = "Show Task History";

	c.onActionClick = function($event,action)
	{
		c.showCommentBox = false;
		if(action=='settle')
			spModal.confirm('Are you sure you want to settle the balance with '+c.data.task.owed_to+'?').then(function(confirmed) {
				if(confirmed)
				{
					c.updateState($event,action);
				}
			});
		else if(action=='reject')
			{
				if(c.comments)
				{
					c.showCommentBox = false;
											c.updateState($event,action);
				}
				else
					{
										c.showCommentBox = true;
				c.PLACEHOLDER_MESSAGE = "Please provide a reason for rejecting the request";
					}
			}
		else
			c.updateState($event,action);

	}
	c.onViewHistory = function()
	{
		c.showHistory = !c.showHistory;
		if(c.showHistory)
			c.historyButton = "Hide Task History";
		else
			c.historyButton = "Show Task History";
	}
	c.updateState = function($event,action)
	{
		$event.stopPropagation();
		c.server.get({
			sys_id:c.sys_id,
			action: action,
			comments: c.comments
		}).then(function(result){
			c.data = result.data;
			c.addDescriptions();
			spUtil.get("widget-form", {table:'sn_splitwise_payments',sys_id: c.sys_id, view:'activity'}).then(function(response) {
				c.activityWidget = response;
			});
		});
	}
	c.addDescriptions = function()
	{
		if(c.data.isApprover)
		{
			if(c.data.showApprovalForm)
			{
				c.data.title = c.data.task.payer + ' paid  ₹'+c.data.task.amount_owed+' to you.';			
				c.data.title += ' Approve payment?';
				c.data.PLACEHOLDER_MESSAGE = 'Add comments for approval request';
			}
			else
			{
				c.data.title = c.data.task.payer + ' owes you ₹'+c.data.task.amount_owed;
			}
		}
		else if(c.data.isPayer)
		{
			c.data.title = 'You owe ₹'+c.data.task.amount_owed+' to '+c.data.task.owed_to;
		}
	}
	c.startup = function()
	{
		if($scope.options && $scope.options.sysId)
			c.sys_id = $scope.options.sysId;
		else if($scope.options && $scope.options.shared)
			c.sys_id = $scope.options.shared.sys_id;

		spUtil.get("widget-form", {table:'sn_splitwise_payments',sys_id: c.sys_id, view:'activity'}).then(function(response) {
			c.activityWidget = response;
		});

		c.server.get({
			sys_id:c.sys_id,
		}).then(function(result)
						{
			c.data= result.data;
			c.addDescriptions();

		});
	}
	c.startup();
}

