sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var isGroupSizeValid = function(memberList)
	{
		var maxCount = gs.getProperty('sn_splitwise.max_group_size');	
		if(!memberList || memberList.length < 2 || memberList.length > maxCount)
		{
			gs.addErrorMessage('List of members should have at least 2 members and at most '+maxCount+' members');
			return false;
		}
		return true;
	};
	var isUserPartOfGroup = function(memberList){
		if(!memberList || memberList.indexOf(gs.getUserID())==-1)
		{
			gs.addErrorMessage('List of members should include yourself');
			return false;
		}
		return true;
	};
	var validateGroupRecord = function()
	{
		var memberList = current.members ? current.members.split(',') : null;
		if(!isGroupSizeValid(memberList) || !isUserPartOfGroup(memberList))
			current.setAbortAction(true);
	};

	validateGroupRecord();
})(current, previous);
