sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Get user id of person who paid for the expense
	var gr = new GlideRecord('sn_splitwise_expense');
	gr.addQuery('expense_id',current.expense_id);
	gr.addQuery('group_id',current.group_id);
	gr.query();
	var owed_to, amount_to_subtract = current.amount_owed;
	if(gr.next())
	{
		owed_to = gr.paid_by;		
	}
	if(owed_to!=current.member)
	{
		//lock 

		//get record of user's balance with the expense payer
		var oldBalance = new GlideRecord('sn_splitwise_balance');
		oldBalance.addQuery('group_id',current.group_id);
		oldBalance.addQuery('member',current.member);
		oldBalance.addQuery('owed_to',owed_to);
		oldBalance.query();

		if(oldBalance.next())
		{ //if balance already exists, add to it
			if(oldBalance.amount - amount_to_subtract > 0)
			{
				oldBalance.amount -= amount_to_subtract;
				oldBalance.update();
			}
			else
				oldBalance.deleteRecord();
		}
		//unlock
	}


})(current, previous);
