sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var calculateBalanceOnUpdate = function(){
		// Get user id of person who paid for the expense
		if(current.paid_by!=current.member)
		{
			var total_amount_owed = current.amount_owed == previous.amount_owed ? 
				current.amount_owed : current.amount_owed - previous.amount_owed;
			//lock 

			//get record of user's balance with the expense payer
			var gr = new GlideRecord('sn_splitwise_balance');
			gr.addQuery('member',current.member);
			gr.addQuery('owed_to',current.paid_by);
			gr.query();

			if(gr.next())
			{ 
				//if user already owes money to this member, update the balance
				gs.info("user already owes to member");
				gr.setValue('amount',parseInt(gr.amount.getValue())+total_amount_owed);
				if(gr.update())
					gs.info("Updated row ...");
				else
					gs.info("didnt update ..");
			}
			else
			{
				gs.info("user does not owe to member");
				//check if the member owes money to this person
				gr  = new GlideRecord('sn_splitwise_balance');
				gr.addQuery('member',current.paid_by);
				gr.addQuery('owed_to',current.member);
				gr.query();
				if(gr.next())
				{
					//if member owes money to this person, subtract from money owed
					gs.info("member owes to user");
					var amount_to_get = parseInt(gr.amount.getValue()) - total_amount_owed;
					if(amount_to_get >0)
					{
						gr.setValue('amount',amount_to_get);
						if(gr.update())
							gs.info("Updated row ...");
						else
							gs.info("didnt update ..");
					}
					else 
					{
						gr.deleteRecord();					
						if(amount_to_get < 0)
						{
							gs.info("inserting row of member owing to user");
							gr = new GlideRecord('sn_splitwise_balance');
							gr.initialize();
							//gr.group_id = current.group_id;
							gr.member = current.member;
							gr.amount = 0-amount_to_get;
							gr.owed_to = current.paid_by;		
							if(gr.insert())
								gs.info("inserted");
							else
								gs.info("not inserted");
						}
					}
				}
				else
				{
					gr = new GlideRecord('sn_splitwise_balance');
					gr.initialize();
					gr.member = current.member;
					gr.amount = total_amount_owed;
					gr.owed_to = current.paid_by;		
					gr.insert();
				}

			}
			//unlock

		}
	};

	calculateBalanceOnUpdate();

})(current, previous);
