sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
    }
	var validateMembers = function(){
		var ga = new GlideAjax('GetMaxGroupSize');
		ga.addParam('sysparm_name','getMaxSize');
		ga.getXML(validateGroupList);

		function validateGroupList(response)
		{
			var maxSize = response.responseXML.documentElement.getAttribute("answer");
			var list = newValue && newValue.split(',');
			if(list && list.length > maxSize)
			{
				g_form.showFieldMsg('members','A group cannot have more than '+maxSize+' members.','error');
			}
			else if(list && list.length < 2)
			{
				g_form.showFieldMsg('members','A group should have at least 2 members.','error');
			}
			else
			{
				if(list.indexOf(g_user.userID)==-1)
				{
					g_form.showFieldMsg('members','Group should include yourself','error');

				}
				else
					g_form.hideFieldMsg('members');
			}
		}
	};
	validateMembers();

}
